.Slot.bottomRight {
    background: #00000000;
    background-image: linear-gradient(to top,#00000000 15%,rgba(255,255,255,0) 15%);
}

.Slot.bottomRight div {
    position: absolute;
    right: 3%;
    bottom: 0%;
}

.Slot.bottomRight div:before, .Slot.bottomRight div:after {
    display: block;
    content: '';
    position: absolute;
}

.Slot.bottomRight {
    text-align: center;
    min-height: 105vh;
    position: relative;
    overflow: hidden;
}

.Slot.bottomRight div {
    width: 14em;
    height: 15em;
    margin-left: -7em;
    margin-top: -4em;
    background-repeat: no-repeat;
    background-image: linear-gradient(to bottom,#ccc .3em,#443e48 .3em),linear-gradient(to bottom,#222 10em,transparent 10em),radial-gradient(ellipse at center,rgba(255,255,255,0.5) 20%,rgba(0,0,0,0) 50%);
    background-size: 93% 8.1em,100% 100%,18em 1.5em;
    background-position: 50% .6em,0 0,left 50% bottom 0;
    border-radius: 5px;
    animation: floating 1500ms infinite ease-in-out;
}

.Slot.bottomRight div:before {
    content: '</>';
    box-sizing: border-box;
    width: 4em;
    height: 11em;
    margin-left: -2em;
    padding-top: 4.5em;
    left: 50%;
    top: 0;
    background-image: linear-gradient(to right,#fff 20%,#007bff 50%,rgba(0,0,0,0) 50%),linear-gradient(to right,#aaa,#ccc,#aaa);
    background-repeat: repeat-x,no-repeat;
    background-size: 100% .3em,.3em 1em;
    background-position: 0 100%,50% 100%;
    animation: fan 60ms infinite linear;
}

.Slot.bottomRight div:after {
    width: 18em;
    height: .7em;
    left: -2em;
